import{_ as a}from"./chunks/admin-ui-redis-stats.e6381fc7.js";import{_ as s}from"./chunks/profiling-redis-CommandAfter.9fd6192a.js";import{_ as t,c as n,o as i,b as e,a as r}from"./app.debf38f2.js";var o="/assets/admin-ui-redis.4f75a9c2.png",d="/assets/admin-ui-redis-connection.3b143781.png",p="/assets/admin-ui-redis-new.1e9ba4cb.png",c="/assets/admin-ui-redis-new-set.d0a0102a.png",l="/assets/admin-ui-redis-set-pretty.557869f4.png",u="/assets/admin-ui-redis-set-preview.d2ab72ec.png",h="/assets/admin-ui-redis-set-edit.2bf6cff9.png",m="/assets/admin-ui-redis-string-pretty.aeca0885.png",g="/assets/admin-ui-redis-string-preview.87962936.png",f="/assets/admin-ui-redis-string-edit.0aececd1.png",b="/assets/admin-ui-redis-hash-edit.3e05eb2b.png",y="/assets/admin-ui-redis-zset-edit.f185b305.png",_="/assets/admin-ui-redis-command.fb311671.png";const U='{"title":"Redis Admin","description":"","frontmatter":{"title":"Redis Admin"},"headers":[{"level":3,"title":"Redis Stats on Dashboard","slug":"redis-stats-on-dashboard"},{"level":2,"title":"Info","slug":"info"},{"level":3,"title":"Modify Redis Connection","slug":"modify-redis-connection"},{"level":2,"title":"Search","slug":"search"},{"level":3,"title":"Set","slug":"set"},{"level":3,"title":"String","slug":"string"},{"level":3,"title":"Hash","slug":"hash"},{"level":3,"title":"Sorted Set","slug":"sorted-set"},{"level":2,"title":"Command","slug":"command"},{"level":2,"title":"Profile App Redis Usage","slug":"profile-app-redis-usage"},{"level":2,"title":"Feedback Welcome","slug":"feedback-welcome"}],"relativePath":"admin-ui-redis.md"}',k={},v=e("p",null,"The Redis Admin UI lets you manage your App's configured Redis Server with a user-friendly UX for managing core Redis data types, including simple search to quickly find Redis values, quick navigation between related values, first class support for JSON values and a flexible command interface and command history to inspect all previously run redis commands that's easily editable & rerun.",-1),w=e("iframe",{class:"video-hd",src:"https://www.youtube.com/embed/K4459zrrxOY",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture",allowfullscreen:""},null,-1),S=r('<h3 id="redis-stats-on-dashboard" tabindex="-1">Redis Stats on Dashboard <a class="header-anchor" href="#redis-stats-on-dashboard" aria-hidden="true">#</a></h3><p>The <a href="/admin-ui#dashboard">Admin UI Dashboard</a> contains valuable insight into monitoring the health of your App&#39;s redis usage with the client &amp; server counters:</p><p><a href="/admin-ui#dashboard"><img src="'+a+'" alt=""></a></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>A description of each of these stats is available in the <a href="/redis/stats">Redis Stats docs</a></p></div><h2 id="info" tabindex="-1">Info <a class="header-anchor" href="#info" aria-hidden="true">#</a></h2><p>The Redis Admin home page shows the output of the Redis <a href="https://redis.io/commands/info/" target="_blank" rel="noopener noreferrer">INFO</a> command containing detailed information on the remote redis server:</p><p><img src="'+o+`" alt=""></p><p>By default it uses the App&#39;s configured database but can easily switch between Redis databases with the numbered Database dropdown.</p><h3 id="modify-redis-connection" tabindex="-1">Modify Redis Connection <a class="header-anchor" href="#modify-redis-connection" aria-hidden="true">#</a></h3><p>Changing your App&#39;s Redis Configuration at runtime can be enabled with:</p><div class="language-csharp"><pre><code>Plugins<span class="token punctuation">.</span><span class="token function">Add</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">AdminRedisFeature</span> <span class="token punctuation">{</span>
    ModifiableConnection <span class="token operator">=</span> <span class="token boolean">true</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>Which will linkify the Redis Connection string to open the <strong>Change Connection</strong> Dialog:</p><p><img src="`+d+'" alt=""></p><p>Be aware this will change your App&#39;s Redis Connection at runtime to different redis server than what it was configured with, which can be useful if you have a warm stand-by Redis server you want to switch to without redeploying your App.</p><h2 id="search" tabindex="-1">Search <a class="header-anchor" href="#search" aria-hidden="true">#</a></h2><p>The Search tab is where you&#39;ll find the primary functionality for being able to quickly search through the Redis keyspace to find where you can create or edit new Redis <a href="https://redis.io/docs/data-types/strings/" target="_blank" rel="noopener noreferrer">Strings</a>, <a href="https://redis.io/docs/data-types/lists/" target="_blank" rel="noopener noreferrer">Lists</a>, <a href="https://redis.io/docs/data-types/sets/" target="_blank" rel="noopener noreferrer">Sets</a>, <a href="https://redis.io/docs/data-types/sorted-sets/" target="_blank" rel="noopener noreferrer">Sorted Sets</a> and <a href="https://redis.io/docs/data-types/hashes/" target="_blank" rel="noopener noreferrer">Hashes</a>:</p><p><img src="'+p+'" alt=""></p><h3 id="set" tabindex="-1">Set <a class="header-anchor" href="#set" aria-hidden="true">#</a></h3><p>Selecting a Redis Data Type displays an optimized form you can use to create a new Value of that type:</p><p><img src="'+c+'" alt=""></p><p>Which you can view in a <strong>Pretty</strong> view where collections like sets are displayed in a formatted JS Array letting you can copy all its values:</p><p><img src="'+l+'" alt=""></p><p>A <strong>Preview</strong> mode displaying the results in a human-friendly table view:</p><p><img src="'+u+'" alt=""></p><p>And an <strong>Edit</strong> mode where you can <strong>add</strong> and <strong>delete</strong> members:</p><p><img src="'+h+'" alt=""></p><h3 id="string" tabindex="-1">String <a class="header-anchor" href="#string" aria-hidden="true">#</a></h3><p>The same functionality is available for all Data Types, whilst <strong>Strings</strong> contain first-class support for JSON strings in their <strong>Pretty</strong>:</p><p><img src="'+m+'" alt=""></p><p><strong>Preview</strong></p><p><img src="'+g+'" alt=""></p><p>and <strong>Edit</strong> views where you can <strong>format JSON</strong> when creating or edit JSON Strings:</p><p><img src="'+f+'" alt=""></p><h3 id="hash" tabindex="-1">Hash <a class="header-anchor" href="#hash" aria-hidden="true">#</a></h3><p>Hashes have the same functionality as <strong>SET</strong> with an additional field to capture the hash entries value:</p><p><img src="'+b+'" alt=""></p><h3 id="sorted-set" tabindex="-1">Sorted Set <a class="header-anchor" href="#sorted-set" aria-hidden="true">#</a></h3><p>Whilst Sorted Sets maintains an extra numerical field to capture Sorted Set scores:</p><p><img src="'+y+'" alt=""></p><h2 id="command" tabindex="-1">Command <a class="header-anchor" href="#command" aria-hidden="true">#</a></h2><p>The Command tab gives you a flexible Command bar letting you run adhoc Redis commands against the selected database. It includes a Command History displaying all previous commands run which you can select on to quickly edit &amp; rerun commands:</p><p><img src="'+_+`" alt=""></p><p>By default it blocks running dangerous and unsuitable commands for a Web interface which can be modified when registering the <code>AdminRedisFeature</code>, which by default prevents the commands below:</p><div class="language-csharp"><pre><code>Plugins<span class="token punctuation">.</span><span class="token function">Add</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">AdminRedisFeature</span> <span class="token punctuation">{</span>
    IllegalCommands <span class="token operator">=</span> <span class="token punctuation">{</span>
        <span class="token string">&quot;BLMOVE&quot;</span><span class="token punctuation">,</span>
        <span class="token string">&quot;BLMPOP&quot;</span><span class="token punctuation">,</span>
        <span class="token string">&quot;BLPOP&quot;</span><span class="token punctuation">,</span>
        <span class="token string">&quot;BRPOP&quot;</span><span class="token punctuation">,</span>
        <span class="token string">&quot;BRPOPLPUSH&quot;</span><span class="token punctuation">,</span>
        <span class="token string">&quot;FLUSHDB&quot;</span><span class="token punctuation">,</span>
        <span class="token string">&quot;FLUSHALL&quot;</span><span class="token punctuation">,</span>
        <span class="token string">&quot;MONITOR&quot;</span><span class="token punctuation">,</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><h2 id="profile-app-redis-usage" tabindex="-1">Profile App Redis Usage <a class="header-anchor" href="#profile-app-redis-usage" aria-hidden="true">#</a></h2><p>The command history maintains a log for all commands executed in the Redis Admin UI, you can inspect the redis commands executed by your Services with the <a href="/admin-ui-profiling#redis-profiling">Redis Profiling</a> built into the <a href="/admin-ui-profiling">Admin Profiling UI</a>.</p><p><a href="/admin-ui-profiling#redis-profiling"><img src="`+s+'" alt=""></a></p><h2 id="feedback-welcome" tabindex="-1">Feedback Welcome <a class="header-anchor" href="#feedback-welcome" aria-hidden="true">#</a></h2><p>We hope you&#39;ll find the Redis Admin feature useful, please let us know what other features you would like in <a href="https://github.com/ServiceStack/Discuss/discussions" target="_blank" rel="noopener noreferrer">ServiceStack/Discuss</a>.</p>',49),R=[v,w,S];function A(P,q,x,T,C,I){return i(),n("div",null,R)}var L=t(k,[["render",A]]);export{U as __pageData,L as default};
